// amplify.ts
import {Auth} from '@aws-amplify/auth';
import {Hub} from '@aws-amplify/core';

export const AMPLIFY_AUTH = new InjectionToken<string>('amplify-auth');
export const AMPLIFY_HUB = new InjectionToken<string>('amplify-hub');

